import React, { useState } from 'react';
import { data } from '../../Assets/images/data';
import classes from './header.module.css';
import ShoppingBasketIcon from '@mui/icons-material/ShoppingBasket';
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import TelegramIcon from '@mui/icons-material/Telegram';
import XIcon from '@mui/icons-material/X';


function Banner() {

   const [menu , setMenu] = useState("menu");

   function underHandler(){
    setMenu()
   }

  return (
    <>
      <di
        className={classes.banner}
        style={{
          height: "100vh",
          backgroundSize: "cover",
          backgroundImage: `url(${data.banner})`,
          backgroundPosition: "left",
          backgroundRepeat: "no-repeat"
        }}
      >
        <div className={classes.nav_container}>
          <div className={classes.nav_container_left}>
            <a href="">
              <img src={data.logo} alt="Logo" />
            </a>
          </div>

          <div className={classes.nav_container_middle}>

          </div> 
          <div className={classes.banner_contents_right}>
            <div className={classes.right_inner}>
              <a href="#"><FacebookIcon /></a>
              <a href="#"><XIcon /></a>
              <a href="#"><InstagramIcon /></a>
              <a className={classes.cart} href="#"><ShoppingBasketIcon /><li>{0}</li></a>
              <button className={classes.nav_btn}>Signin</button>
            </div>
          </div>
        </div>

        <div className={classes.banner_contents}>
          <div className={classes.grid_banner}>
            <h1 className={classes.title}>Fidel Restaurant</h1>
            <p className={classes.animated_sentence}>
            "Welcome to Fidel Restaurant, 
            where delicious flavors meet joyful gatherings, 
            creating unforgettable moments with every bite!"
            </p>
          </div>
        </div>
      </di>
    </>
  );
}

export default Banner;
